sp_widget.client_script:
function($rootScope) {
	/* widget controller */
	var c = this;
	/*
	c.remove = function(i) {	
		c.data.notes.splice(i, 1);
	};
	*/
	c.data.selected = "";
	toUpdateidx = 0;
	c.selectItem = function(idx)
	{
		toUpdateidx = idx;
		id = c.data.notes[idx].sys_id;
		c.data.selected = id
		//console.log('Note ID: '+id);
		//$rootScope.noteID = id;
		$rootScope.$emit('selectedNote', id)
	}
	$rootScope.$on("updatedNote", function(event, updatedNote){
		if (c.data.notes[toUpdateidx].sys_id == updatedNote.sys_id)
		{
			c.data.notes[toUpdateidx].title = updatedNote.title;
			c.data.notes[toUpdateidx].note = updatedNote.note.slice(0,20);
			//console.log("Note updated:"+updatedNote.sys_id+"index "+toUpdateidx)
		}
	});
}

sp_widget.script:
(function() {
	data.notes = []
	var noteGR = new GlideRecord("x_snc_createnotes_note");
	noteGR.addQuery('user', gs.getUserID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while(noteGR.next())
	{
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, "number, title, sys_id")
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}
})();
